import React, { useState } from 'react';
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  Grid,
  MenuItem,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Snackbar,
  Alert,
  useTheme,
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';

const faculties = ['Prof. A', 'Prof. B', 'Prof. C'];
const subjects = ['Maths', 'Physics', 'Chemistry'];
const batches = ['Batch 1', 'Batch 2', 'Batch 3'];
const classrooms = ['Room 101', 'Room 102', 'Room 103'];

const AdminSchedule = () => {
  const theme = useTheme();

  const [formData, setFormData] = useState({
    classroom: '',
    batch: '',
    subject: '',
    faculty: '',
    classesPerWeek: '',
  });

  const [scheduleList, setScheduleList] = useState([]);
  const [editIndex, setEditIndex] = useState(-1);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const validateForm = () => {
    const { classroom, batch, subject, faculty, classesPerWeek } = formData;
    return (
      classroom && batch && subject && faculty && classesPerWeek && Number(classesPerWeek) > 0
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateForm()) {
      setSnackbar({ open: true, message: 'Please fill all fields correctly.', severity: 'error' });
      return;
    }

    if (editIndex >= 0) {
      const updatedList = [...scheduleList];
      updatedList[editIndex] = formData;
      setScheduleList(updatedList);
      setEditIndex(-1);
      setSnackbar({ open: true, message: 'Schedule updated successfully!', severity: 'success' });
    } else {
      setScheduleList((prev) => [...prev, formData]);
      setSnackbar({ open: true, message: 'Schedule added successfully!', severity: 'success' });
    }

    setFormData({
      classroom: '',
      batch: '',
      subject: '',
      faculty: '',
      classesPerWeek: '',
    });
  };

  const handleEdit = (index) => {
    setFormData(scheduleList[index]);
    setEditIndex(index);
  };

  const handleDelete = (index) => {
    const updatedList = scheduleList.filter((_, i) => i !== index);
    setScheduleList(updatedList);
    setSnackbar({ open: true, message: 'Schedule deleted.', severity: 'info' });
    if (editIndex === index) {
      setEditIndex(-1);
      setFormData({
        classroom: '',
        batch: '',
        subject: '',
        faculty: '',
        classesPerWeek: '',
      });
    }
  };

  const handleSnackbarClose = () => {
    setSnackbar({ ...snackbar, open: false });
  };

  return (
    <Container sx={{ mt: 4, mb: 6 }}>
      <Paper elevation={6} sx={{ p: 6, borderRadius: 3 }}>
        <Typography variant="h4" gutterBottom fontWeight="bold" sx={{ color: theme.palette.primary.main }}>
          Admin Schedule Management
        </Typography>
        <Box component="form" onSubmit={handleSubmit} noValidate>
          <Grid container spacing={4} alignItems="center">
            <Grid item xs={12} sm={6} md={3}>
              <TextField
                select
                label="Classroom"
                name="classroom"
                value={formData.classroom}
                onChange={handleChange}
                fullWidth
                required
                size="large"
                sx={{ '& .MuiInputBase-input': { fontSize: 18, padding: '14px 12px' } }}
              >
                {classrooms.map((room) => (
                  <MenuItem key={room} value={room}>
                    {room}
                  </MenuItem>
                ))}
              </TextField>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <TextField
                select
                label="Batch"
                name="batch"
                value={formData.batch}
                onChange={handleChange}
                fullWidth
                required
                size="large"
                sx={{ '& .MuiInputBase-input': { fontSize: 18, padding: '14px 12px' } }}
              >
                {batches.map((batch) => (
                  <MenuItem key={batch} value={batch}>
                    {batch}
                  </MenuItem>
                ))}
              </TextField>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <TextField
                select
                label="Subject"
                name="subject"
                value={formData.subject}
                onChange={handleChange}
                fullWidth
                required
                size="large"
                sx={{ '& .MuiInputBase-input': { fontSize: 18, padding: '14px 12px' } }}
              >
                {subjects.map((subj) => (
                  <MenuItem key={subj} value={subj}>
                    {subj}
                  </MenuItem>
                ))}
              </TextField>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <TextField
                select
                label="Faculty"
                name="faculty"
                value={formData.faculty}
                onChange={handleChange}
                fullWidth
                required
                size="large"
                sx={{ '& .MuiInputBase-input': { fontSize: 18, padding: '14px 12px' } }}
              >
                {faculties.map((faculty) => (
                  <MenuItem key={faculty} value={faculty}>
                    {faculty}
                  </MenuItem>
                ))}
              </TextField>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <TextField
                type="number"
                label="Classes per Week"
                name="classesPerWeek"
                value={formData.classesPerWeek}
                onChange={handleChange}
                fullWidth
                required
                InputProps={{ inputProps: { min: 1, max: 10 } }}
                size="large"
                sx={{ '& .MuiInputBase-input': { fontSize: 18, padding: '14px 12px' } }}
              />
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <Button
                variant="contained"
                color={editIndex >= 0 ? 'secondary' : 'primary'}
                type="submit"
                fullWidth
                sx={{ height: 60, fontWeight: 'bold', fontSize: 18 }}
              >
                {editIndex >= 0 ? 'Update Schedule' : 'Add Schedule'}
              </Button>
            </Grid>
          </Grid>
        </Box>

        {scheduleList.length > 0 && (
          <TableContainer component={Paper} sx={{ mt: 7 }}>
            <Table sx={{ minWidth: 670 }} aria-label="schedule table">
              <TableHead>
                <TableRow sx={{ backgroundColor: theme.palette.grey[200] }}>
                  <TableCell sx={{ fontWeight: 'bold', fontSize: 18 }}>Classroom</TableCell>
                  <TableCell sx={{ fontWeight: 'bold', fontSize: 18 }}>Batch</TableCell>
                  <TableCell sx={{ fontWeight: 'bold', fontSize: 18 }}>Subject</TableCell>
                  <TableCell sx={{ fontWeight: 'bold', fontSize: 18 }}>Faculty</TableCell>
                  <TableCell sx={{ fontWeight: 'bold', fontSize: 18 }} align="center">
                    Classes/Week
                  </TableCell>
                  <TableCell sx={{ fontWeight: 'bold', fontSize: 18 }} align="center">
                    Actions
                  </TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {scheduleList.map((item, index) => (
                  <TableRow
                    key={index}
                    sx={{
                      '&:nth-of-type(odd)': { backgroundColor: theme.palette.grey[50] },
                      '&:hover': { backgroundColor: theme.palette.action.hover },
                    }}
                  >
                    <TableCell sx={{ fontSize: 17, paddingY: 2 }}>{item.classroom}</TableCell>
                    <TableCell sx={{ fontSize: 17, paddingY: 2 }}>{item.batch}</TableCell>
                    <TableCell sx={{ fontSize: 17, paddingY: 2 }}>{item.subject}</TableCell>
                    <TableCell sx={{ fontSize: 17, paddingY: 2 }}>{item.faculty}</TableCell>
                    <TableCell sx={{ fontSize: 17, paddingY: 2, textAlign: 'center' }}>{item.classesPerWeek}</TableCell>
                    <TableCell sx={{ textAlign: 'center' }}>
                      <IconButton
                        color="primary"
                        aria-label="edit"
                        onClick={() => handleEdit(index)}
                        size="large"
                      >
                        <EditIcon fontSize="inherit" />
                      </IconButton>
                      <IconButton
                        color="error"
                        aria-label="delete"
                        onClick={() => handleDelete(index)}
                        size="large"
                        sx={{ ml: 1 }}
                      >
                        <DeleteIcon fontSize="inherit" />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}

        <Snackbar
          open={snackbar.open}
          autoHideDuration={3000}
          onClose={handleSnackbarClose}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        >
          <Alert onClose={handleSnackbarClose} severity={snackbar.severity} sx={{ width: '100%' }}>
            {snackbar.message}
          </Alert>
        </Snackbar>
      </Paper>
    </Container>
  );
};

export default AdminSchedule;
