import React from 'react';
import {
  Container,
  Typography,
  Box,
  Button,
  Grid,
  Paper,
  SvgIcon,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import ScheduleIcon from '@mui/icons-material/Schedule';
import LogoutIcon from '@mui/icons-material/Logout';
import DashboardIcon from '@mui/icons-material/Dashboard';

const AdminDashboard = () => {
  const navigate = useNavigate();

  const navItems = [
    {
      label: 'Manage Schedule',
      icon: <ScheduleIcon fontSize="large" color="primary" />,
      onClick: () => navigate('/admin/schedule'),
    },
    {
      label: 'Overview',
      icon: <DashboardIcon fontSize="large" color="primary" />,
      onClick: () => alert('Overview feature coming soon!'),
    },
    {
      label: 'Logout',
      icon: <LogoutIcon fontSize="large" color="error" />,
      onClick: () => navigate('/'),
    },
  ];

  return (
    <Container maxWidth="lg" sx={{ mt: 8 }}>
      <Typography variant="h3" component="h1" gutterBottom fontWeight="bold" color="primary">
        Admin Dashboard
      </Typography>
      <Typography variant="body1" gutterBottom color="textSecondary" sx={{ mb: 4 }}>
        Welcome, Admin! Use the options below to manage your institution's schedules and settings.
      </Typography>

      <Grid container spacing={4}>
        {navItems.map((item) => (
          <Grid key={item.label} item xs={12} sm={6} md={4}>
            <Paper
              elevation={8}
              sx={{
                p: 3,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                borderRadius: 3,
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                '&:hover': { boxShadow: 12, bgcolor: 'primary.light' },
              }}
              onClick={item.onClick}
              role="button"
              tabIndex={0}
              onKeyPress={(e) => {
                if (e.key === 'Enter') item.onClick();
              }}
            >
              {item.icon}
              <Typography variant="h6" sx={{ mt: 2, fontWeight: 'medium' }}>
                {item.label}
              </Typography>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default AdminDashboard;
