import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Paper,
  Grid,
  useTheme,
  Button,
  Box,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const DAYS = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday'];
const PERIODS = [1, 2, 3, 4, 5, 6];

const TeacherSchedule = () => {
  const theme = useTheme();
  const navigate = useNavigate();
  const API = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';

  const [schedule, setSchedule] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const userId = localStorage.getItem('user_id');
    if (!userId) { setLoading(false); return; }
    const load = async () => {
      const res = await fetch(`${API}/timetables/teacher/${userId}`);
      const data = await res.json();
      setSchedule(data?.schedule || {});
      setLoading(false);
    };
    load().catch(() => setLoading(false));
  }, [API]);

  const renderCell = (day, period) => {
    const dayArr = schedule?.[day] || [];
    const sess = dayArr.find((s) => s.period === period);
    if (!sess) return null;
    return (
      <>
        <Typography variant="subtitle2">{sess.subject_name}</Typography>
        <Typography variant="caption">{sess.class_name}{sess.room ? ` • ${sess.room}` : ''}</Typography>
      </>
    );
  };

  return (
    <Container sx={{ mt: 6, mb: 6 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="h4" fontWeight="bold" color={theme.palette.primary.main}>
          Teacher Schedule
        </Typography>
        <Button variant="contained" color="secondary" onClick={() => navigate('/teacher/leave')}>
          Apply Leave
        </Button>
      </Box>

      <Paper elevation={6} sx={{ p: 3, borderRadius: 3, overflowX: 'auto' }}>
        <Grid container spacing={0} sx={{ minWidth: 720 }}>
          <Grid item xs={2} sx={{ borderBottom: 1, borderRight: 1, borderColor: 'divider', p: 1 }}>
            <Typography variant="subtitle1" fontWeight="bold">Time / Day</Typography>
          </Grid>
          {DAYS.map((day) => (
            <Grid key={day} item xs={2} sx={{ borderBottom: 1, borderRight: 1, borderColor: 'divider', backgroundColor: theme.palette.action.hover, p: 1, textAlign: 'center' }}>
              <Typography variant="subtitle1" fontWeight="bold">{day}</Typography>
            </Grid>
          ))}
          {PERIODS.map((p) => (
            <React.Fragment key={p}>
              <Grid item xs={2} sx={{ borderBottom: 1, borderRight: 1, borderColor: 'divider', p: 1, fontWeight: 'medium' }}>{`Period ${p}`}</Grid>
              {DAYS.map((day) => (
                <Grid key={`${day}-${p}`} item xs={2} sx={{ borderBottom: 1, borderRight: 1, borderColor: 'divider', p: 1, minHeight: 60 }}>
                  {renderCell(day, p)}
                </Grid>
              ))}
            </React.Fragment>
          ))}
        </Grid>
      </Paper>
    </Container>
  );
};

export default TeacherSchedule;
