import React, { useEffect, useMemo, useState } from 'react';
import {
  Container,
  Typography,
  Paper,
  Grid,
  useTheme,
  Button,
  Box,
  Alert,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import PageHeader from '../components/PageHeader';
import EventNoteIcon from '@mui/icons-material/EventNote';

const DAYS = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday'];
const PERIODS = [1, 2, 3, 4, 5, 6];

const TeacherSchedule = () => {
  const theme = useTheme();
  const navigate = useNavigate();
  const API = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';

  const [schedule, setSchedule] = useState({});
  const [loading, setLoading] = useState(true);
  const [quota, setQuota] = useState(null);
  const [teachers, setTeachers] = useState([]);
  const [overrideTeacherId, setOverrideTeacherId] = useState('');

  const isDev = (process.env.NODE_ENV !== 'production') || process.env.REACT_APP_DEV_TOOLS === '1';

  const loadForTeacher = async (teacherId) => {
    const [ttRes, quotaRes] = await Promise.all([
      fetch(`${API}/timetables/teacher/${teacherId}`),
      fetch(`${API}/leaves/quota/${teacherId}`),
    ]);
    const [tt, quotaData] = await Promise.all([ttRes.json(), quotaRes.json()]);
    setSchedule(tt?.schedule || {});
    setQuota(quotaData);
  };

  useEffect(() => {
    const userId = localStorage.getItem('user_id');
    if (!userId) { setLoading(false); return; }
    const init = async () => {
      if (isDev) {
        // load a small list of teachers for dev picker
        try {
          const res = await fetch(`${API}/users`);
          const all = await res.json();
          setTeachers(all.filter(u => u.role === 'teacher'));
        } catch (_) {}
      }
      await loadForTeacher(overrideTeacherId || userId);
      setLoading(false);
    };
    init().catch(() => setLoading(false));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [API]);

  useEffect(() => {
    const userId = localStorage.getItem('user_id');
    if (!userId) return;
    if (overrideTeacherId) {
      loadForTeacher(overrideTeacherId).catch(() => {});
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [overrideTeacherId]);

  const isEmpty = useMemo(() => {
    if (!schedule) return true;
    return DAYS.every(d => (schedule[d] || []).length === 0);
  }, [schedule]);

  const renderCell = (day, period) => {
    const dayArr = schedule?.[day] || [];
    const sess = dayArr.find((s) => s.period === period);
    if (!sess) return null;
    return (
      <>
        <Typography variant="subtitle2">{sess.subject_name}</Typography>
        <Typography variant="caption">{sess.class_name}{sess.room ? ` â€¢ ${sess.room}` : ''}</Typography>
      </>
    );
  };

  return (
    <Container sx={{ mt: 6, mb: 6 }}>
      <PageHeader
        role="teacher"
        icon={<EventNoteIcon sx={{ fontSize: 40 }} />}
        title="Teacher Schedule"
        subtitle="Your weekly periods across classes"
        action={(
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            {quota && (
              <Typography variant="subtitle2" color="text.secondary">
                Remaining leave days: {quota.remaining}/{quota.limit}
              </Typography>
            )}
            {isDev && teachers.length > 0 && (
              <FormControl size="small" sx={{ minWidth: 220 }}>
                <InputLabel id="teacher-picker-label">Preview teacher</InputLabel>
                <Select
                  labelId="teacher-picker-label"
                  label="Preview teacher"
                  value={overrideTeacherId}
                  onChange={(e) => setOverrideTeacherId(e.target.value)}
                >
                  <MenuItem value="">(Logged-in)</MenuItem>
                  {teachers.map(t => (
                    <MenuItem key={t.id} value={String(t.id)}>{`${t.first_name || ''} ${t.last_name || ''}`.trim()} â€” #{t.id}</MenuItem>
                  ))}
                </Select>
              </FormControl>
            )}
            <Button variant="contained" color="secondary" onClick={() => navigate('/teacher/leave')}>
              Apply Leave
            </Button>
          </Box>
        )}
      />

      {isEmpty && !loading && (
        <Alert severity="info" sx={{ mb: 2 }}>
          You have no assigned classes in the current timetable. If this is unexpected, ask an admin to regenerate timetables or try a different teacher via the preview picker (dev only).
        </Alert>
      )}

      <Paper elevation={6} sx={{ p: 3, borderRadius: 3, overflowX: 'auto' }}>
        <Grid container spacing={0} sx={{ minWidth: 720 }}>
          <Grid item xs={2} sx={{ borderBottom: 1, borderRight: 1, borderColor: 'divider', p: 1 }}>
            <Typography variant="subtitle1" fontWeight="bold">Time / Day</Typography>
          </Grid>
          {DAYS.map((day) => (
            <Grid key={day} item xs={2} sx={{ borderBottom: 1, borderRight: 1, borderColor: 'divider', backgroundColor: theme.palette.action.hover, p: 1, textAlign: 'center' }}>
              <Typography variant="subtitle1" fontWeight="bold">{day}</Typography>
            </Grid>
          ))}
          {PERIODS.map((p) => (
            <React.Fragment key={p}>
              <Grid item xs={2} sx={{ borderBottom: 1, borderRight: 1, borderColor: 'divider', p: 1, fontWeight: 'medium' }}>{`Period ${p}`}</Grid>
              {DAYS.map((day) => (
                <Grid key={`${day}-${p}`} item xs={2} sx={{ borderBottom: 1, borderRight: 1, borderColor: 'divider', p: 1, minHeight: 60 }}>
                  {renderCell(day, p)}
                </Grid>
              ))}
            </React.Fragment>
          ))}
        </Grid>
      </Paper>
    </Container>
  );
};

export default TeacherSchedule;
