import { createTheme } from '@mui/material/styles';

const grad = (a, b) => `linear-gradient(135deg, ${a} 0%, ${b} 100%)`;

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: { main: '#1e3a8a' }, // navy
    secondary: { main: '#7c3aed' }, // violet
    success: { main: '#16a34a' },
    warning: { main: '#f59e0b' },
    error: { main: '#ef4444' },
    background: {
      default: '#f5f7fb',
      paper: '#ffffff',
    },
  },
  shape: { borderRadius: 16 },
  typography: {
    fontFamily: '"Roboto", "Inter", "Helvetica", "Arial", sans-serif',
    h3: { fontWeight: 800, letterSpacing: -0.5 },
    h4: { fontWeight: 800, letterSpacing: -0.2 },
    h5: { fontWeight: 700 },
  },
  components: {
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          backgroundImage:
            'radial-gradient(40rem 40rem at 10% -10%, rgba(124,58,237,0.12), transparent 60%), ' +
            'radial-gradient(30rem 30rem at 110% 10%, rgba(14,165,233,0.12), transparent 60%)',
          backgroundAttachment: 'fixed',
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: 18,
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: { fontWeight: 700, borderRadius: 999 },
        containedPrimary: {
          color: '#fff',
          backgroundImage: grad('#2563eb', '#7c3aed'),
          boxShadow: '0 8px 20px rgba(124,58,237,0.25)',
        },
        containedSecondary: {
          color: '#fff',
          backgroundImage: grad('#7c3aed', '#ec4899'),
          boxShadow: '0 8px 20px rgba(236,72,153,0.25)',
        },
      },
    },
    MuiTabs: {
      styleOverrides: {
        indicator: { height: 4, borderRadius: 4 },
      },
    },
    MuiChip: {
      styleOverrides: {
        root: { fontWeight: 600 },
      },
    },
  },
});

export default theme;
