import React, { useState } from 'react';
import {
  Container,
  Typography,
  Paper,
  AppBar,
  Tabs,
  Tab,
  Box,
  useTheme,
} from '@mui/material';
import SchoolIcon from '@mui/icons-material/School';
import NotificationsActiveIcon from '@mui/icons-material/NotificationsActive';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import LogoutIcon from '@mui/icons-material/Logout';
import { useNavigate } from 'react-router-dom';

const tabProps = (index) => ({
  id: `student-tab-${index}`,
  'aria-controls': `student-tabpanel-${index}`,
});

const StudentDashboard = () => {
  const theme = useTheme();
  const navigate = useNavigate();
  const [value, setValue] = useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
    switch (newValue) {
      case 0:
        navigate('/student/timetable');
        break;
      case 1:
        navigate('/student/notifications');
        break;
      case 2:
        navigate('/student/profile');
        break;
      case 3:
        navigate('/');
        break;
      default:
        break;
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 6 }}>
      <Typography variant="h3" fontWeight="bold" gutterBottom color={theme.palette.primary.main}>
        Student Dashboard
      </Typography>
      <Paper elevation={8} sx={{ borderRadius: 3 }}>
        <AppBar position="static" sx={{ bgcolor: theme.palette.background.paper }}>
          <Tabs
            value={value}
            onChange={handleChange}
            aria-label="student dashboard tabs"
            indicatorColor="primary"
            textColor="primary"
            variant="fullWidth"
            sx={{ '& .MuiTab-root': { fontWeight: 'bold', fontSize: 16 } }}
          >
            <Tab icon={<SchoolIcon />} iconPosition="start" label="View Timetable" {...tabProps(0)} />
            <Tab icon={<NotificationsActiveIcon />} iconPosition="start" label="Notifications" {...tabProps(1)} />
            <Tab icon={<AccountCircleIcon />} iconPosition="start" label="Profile" {...tabProps(2)} />
            <Tab icon={<LogoutIcon />} iconPosition="start" label="Logout" {...tabProps(3)} />
          </Tabs>
        </AppBar>
        <Box sx={{ p: 4 }}>
          {value === 0 && <Typography>View your class timetable here.</Typography>}
          {value === 1 && <Typography>Check your notifications.</Typography>}
          {value === 2 && <Typography>Manage your profile.</Typography>}
          {value === 3 && <Typography>Logging out...</Typography>}
        </Box>
      </Paper>
    </Container>
  );
};

export default StudentDashboard;
