import React from 'react';
import {
  Container,
  Typography,
  Box,
  Button,
  Grid,
  Paper,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import EventNoteIcon from '@mui/icons-material/EventNote';
import LogoutIcon from '@mui/icons-material/Logout';
import SchoolIcon from '@mui/icons-material/School';

const TeacherDashboard = () => {
  const navigate = useNavigate();

  const navItems = [
    {
      label: 'My Schedule',
      icon: <EventNoteIcon fontSize="large" color="primary" />,
      onClick: () => alert('Feature coming soon!'),
    },
    {
      label: 'Attendance',
      icon: <SchoolIcon fontSize="large" color="primary" />,
      onClick: () => alert('Feature coming soon!'),
    },
    {
      label: 'Logout',
      icon: <LogoutIcon fontSize="large" color="error" />,
      onClick: () => navigate('/'),
    },
    // Inside navItems array, add:
    {
      label: 'View Schedule',
      icon: <EventNoteIcon fontSize="large" color="primary" />,
      onClick: () => navigate('/teacher/schedule'),
    },
  ];

  return (
    <Container maxWidth="lg" sx={{ mt: 8 }}>
      <Typography variant="h3" component="h1" gutterBottom fontWeight="bold" color="primary">
        Teacher Dashboard
      </Typography>
      <Typography variant="body1" gutterBottom color="textSecondary" sx={{ mb: 4 }}>
        Welcome, Teacher! Access your schedule, attendance, and other tools here.
      </Typography>

      <Grid container spacing={4}>
        {navItems.map((item) => (
          <Grid key={item.label} item xs={12} sm={6} md={4}>
            <Paper
              elevation={8}
              sx={{
                p: 3,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                borderRadius: 3,
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                '&:hover': { boxShadow: 12, bgcolor: 'primary.light' },
              }}
              onClick={item.onClick}
              role="button"
              tabIndex={0}
              onKeyPress={(e) => {
                if (e.key === 'Enter') item.onClick();
              }}
            >
              {item.icon}
              <Typography variant="h6" sx={{ mt: 2, fontWeight: 'medium' }}>
                {item.label}
              </Typography>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default TeacherDashboard;
