import { createTheme } from '@mui/material/styles';

const grad = (a, b) => `linear-gradient(135deg, ${a} 0%, ${b} 100%)`;

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: { main: '#14b8a6' }, // teal
    secondary: { main: '#f59e0b' }, // amber
    success: { main: '#22c55e' },
    warning: { main: '#f59e0b' },
    error: { main: '#ef4444' },
    background: {
      default: '#fafbff',
      paper: '#ffffff',
    },
  },
  shape: { borderRadius: 16 },
  typography: {
    fontFamily: '"Roboto", "Inter", "Helvetica", "Arial", sans-serif',
    h3: { fontWeight: 800, letterSpacing: -0.5 },
    h4: { fontWeight: 800, letterSpacing: -0.2 },
    h5: { fontWeight: 700 },
  },
  components: {
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          backgroundImage:
            'radial-gradient(40rem 40rem at 10% -10%, rgba(16,185,129,0.10), transparent 60%), ' +
            'radial-gradient(30rem 30rem at 110% 10%, rgba(245,158,11,0.10), transparent 60%)',
          backgroundAttachment: 'fixed',
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: 18,
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: { fontWeight: 700, borderRadius: 999 },
        containedPrimary: {
          color: '#0b3f3a',
          backgroundImage: grad('#a7f3d0', '#fde68a'), // mint to amber, light
          boxShadow: '0 8px 20px rgba(20,184,166,0.18)',
        },
        containedSecondary: {
          color: '#7c2d12',
          backgroundImage: grad('#fde68a', '#ffedd5'),
          boxShadow: '0 8px 20px rgba(245,158,11,0.18)',
        },
      },
    },
    MuiTabs: {
      styleOverrides: {
        indicator: { height: 4, borderRadius: 4 },
      },
    },
    MuiChip: {
      styleOverrides: {
        root: { fontWeight: 600 },
      },
    },
  },
});

export default theme;
