import React from 'react';
import { Paper, Box, Typography } from '@mui/material';

const THEMES = {
  admin: { from: '#d1fae5', to: '#fef3c7', text: '#065f46', border: 'rgba(6,95,70,0.15)' },
  teacher: { from: '#ffedd5', to: '#fde68a', text: '#7c2d12', border: 'rgba(124,45,18,0.15)' },
  student: { from: '#dcfce7', to: '#e0f2fe', text: '#14532d', border: 'rgba(20,83,45,0.15)' },
};

export default function PageHeader({ role = 'admin', icon, title, subtitle, action }) {
  const t = THEMES[role] || THEMES.admin;
  return (
    <Paper elevation={0} sx={{
      p: 3,
      mb: 3,
      borderRadius: 4,
      background: `linear-gradient(135deg, ${t.from}, ${t.to})`,
      border: `1px solid ${t.border}`,
      color: t.text,
    }}>
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <Box sx={{ fontSize: 0, color: t.text }}>{icon}</Box>
          <Box>
            <Typography variant="h4" fontWeight={800} sx={{ color: t.text }}>{title}</Typography>
            {subtitle && (
              <Typography variant="body2" sx={{ opacity: 0.9, color: t.text }}>{subtitle}</Typography>
            )}
          </Box>
        </Box>
        {action && <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>{action}</Box>}
      </Box>
    </Paper>
  );
}
