import React, { useState } from 'react';
import {
  Container,
  Typography,
  Box,
  AppBar,
  Tabs,
  Tab,
  Paper,
  useTheme,
} from '@mui/material';
import EventNoteIcon from '@mui/icons-material/EventNote';
import SchoolIcon from '@mui/icons-material/School';
import LogoutIcon from '@mui/icons-material/Logout';
import { useNavigate } from 'react-router-dom';

const tabProps = (index) => ({
  id: `teacher-tab-${index}`,
  'aria-controls': `teacher-tabpanel-${index}`,
});

const TeacherDashboard = () => {
  const theme = useTheme();
  const navigate = useNavigate();
  const [value, setValue] = useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
    switch (newValue) {
      case 0:
        navigate('/teacher/schedule');
        break;
      case 1:
        alert('Attendance feature coming soon!');
        break;
      case 2:
        navigate('/');
        break;
      default:
        break;
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 6 }}>
      <Typography variant="h3" fontWeight="bold" gutterBottom color={theme.palette.primary.main}>
        Teacher Dashboard
      </Typography>
      <Paper elevation={8} sx={{ borderRadius: 3 }}>
        <AppBar position="static" sx={{ bgcolor: theme.palette.background.paper }}>
          <Tabs
            value={value}
            onChange={handleChange}
            aria-label="teacher dashboard tabs"
            indicatorColor="primary"
            textColor="primary"
            variant="fullWidth"
            sx={{ '& .MuiTab-root': { fontWeight: 'bold', fontSize: 16 } }}
          >
            <Tab icon={<EventNoteIcon />} iconPosition="start" label="View Schedule" {...tabProps(0)} />
            <Tab icon={<SchoolIcon />} iconPosition="start" label="Attendance" {...tabProps(1)} />
            <Tab icon={<LogoutIcon />} iconPosition="start" label="Logout" {...tabProps(2)} />
          </Tabs>
        </AppBar>
        <Box sx={{ p: 4 }}>
          {value === 0 && <Typography>Use the tabs to navigate between pages.</Typography>}
          {value === 1 && <Typography>Attendance feature coming soon.</Typography>}
          {value === 2 && <Typography>Logging out...</Typography>}
        </Box>
      </Paper>
    </Container>
  );
};

export default TeacherDashboard;
