import React, { useEffect, useMemo, useState } from 'react';
import {
  Container,
  Typography,
  Paper,
  Grid,
  useTheme,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Button,
  Chip,
  Tooltip,
} from '@mui/material';

const DAYS = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday'];
const PERIODS = [1, 2, 3, 4, 5, 6];

const colorForSubject = (id) => `hsl(${(id * 47) % 360} 85% 45%)`;

const StudentTimetable = () => {
  const theme = useTheme();
  const API = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';

  const [classes, setClasses] = useState([]);
  const [subjects, setSubjects] = useState({});
  const [selectedClass, setSelectedClass] = useState('');
  const [schedule, setSchedule] = useState({});

  useEffect(() => {
    // Load classes and subjects list
    const load = async () => {
      const [clsRes, subRes] = await Promise.all([
        fetch(`${API}/classes`),
        fetch(`${API}/subjects`),
      ]);
      const [cls, subs] = await Promise.all([clsRes.json(), subRes.json()]);
      setClasses(cls);
      const map = {};
      subs.forEach((s) => (map[s.id] = s.name));
      setSubjects(map);
    };
    load().catch(() => {});
  }, [API]);

  const handleLoadTimetable = async () => {
    if (!selectedClass) return;
    const res = await fetch(`${API}/timetables/class/${selectedClass}`);
    const data = await res.json();
    setSchedule(data?.schedule || {});
  };

  const uniqueSubjects = useMemo(() => {
    const set = new Set();
    for (const day of DAYS) {
      for (const s of (schedule?.[day] || [])) set.add(s.subject_id);
    }
    return Array.from(set);
  }, [schedule]);

  const renderCell = (day, period) => {
    const dayArr = schedule?.[day] || [];
    const sess = dayArr.find((s) => s.period === period);
    if (!sess) return null;
    const name = subjects[sess.subject_id] || `SUB-${sess.subject_id}`;
    const bg = colorForSubject(sess.subject_id);
    return (
      <Tooltip title={sess.room ? `Room ${sess.room}` : ''} placement="top" arrow>
        <Chip
          label={name}
          size="small"
          sx={{
            fontWeight: 700,
            color: '#fff',
            backgroundColor: bg,
            maxWidth: '100%',
          }}
        />
      </Tooltip>
    );
  };

  return (
    <Container sx={{ mt: 6, mb: 6 }}>
      <Typography variant="h4" fontWeight="bold" gutterBottom color={theme.palette.primary.main}>
        Student Timetable
      </Typography>

      <Box sx={{ display: 'flex', gap: 2, mb: 2, alignItems: 'center' }}>
        <FormControl sx={{ minWidth: 240 }}>
          <InputLabel id="class-select-label">Class</InputLabel>
          <Select
            labelId="class-select-label"
            label="Class"
            value={selectedClass}
            onChange={(e) => setSelectedClass(e.target.value)}
          >
            {classes.map((c) => (
              <MenuItem key={c.id} value={c.id}>
                {c.name}{c.section ? ` - ${c.section}` : ''}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
        <Button variant="contained" onClick={handleLoadTimetable} disabled={!selectedClass}>
          Load Timetable
        </Button>
      </Box>

      {uniqueSubjects.length > 0 && (
        <Paper variant="outlined" sx={{ p: 1.5, mb: 2 }}>
          <Typography variant="caption" sx={{ mr: 1, color: 'text.secondary' }}>Legend:</Typography>
          <Box sx={{ display: 'inline-flex', flexWrap: 'wrap', gap: 1 }}>
            {uniqueSubjects.map((sid) => (
              <Chip key={sid} label={subjects[sid] || `SUB-${sid}`} size="small" sx={{ color: '#fff', backgroundColor: colorForSubject(sid) }} />
            ))}
          </Box>
        </Paper>
      )}

      <Paper elevation={8} sx={{ p: 3, borderRadius: 3, overflowX: 'auto' }}>
        <Grid container spacing={0} sx={{ minWidth: 720 }}>
          <Grid item xs={2} sx={{ borderBottom: 1, borderRight: 1, borderColor: 'divider', p: 1 }}>
            <Typography variant="subtitle1" fontWeight="bold">Time / Day</Typography>
          </Grid>
          {DAYS.map((day) => (
            <Grid key={day} item xs={2} sx={{ borderBottom: 1, borderRight: 1, borderColor: 'divider', background: 'linear-gradient(180deg, rgba(124,58,237,0.08), rgba(0,0,0,0))', p: 1, textAlign: 'center' }}>
              <Typography variant="subtitle1" fontWeight="bold">{day}</Typography>
            </Grid>
          ))}
          {PERIODS.map((p) => (
            <React.Fragment key={p}>
              <Grid item xs={2} sx={{ borderBottom: 1, borderRight: 1, borderColor: 'divider', p: 1, fontWeight: 'medium' }}>{`Period ${p}`}</Grid>
              {DAYS.map((day) => (
                <Grid key={`${day}-${p}`} item xs={2} sx={{ borderBottom: 1, borderRight: 1, borderColor: 'divider', p: 1, minHeight: 60, display: 'flex', alignItems: 'center' }}>
                  {renderCell(day, p)}
                </Grid>
              ))}
            </React.Fragment>
          ))}
        </Grid>
      </Paper>
    </Container>
  );
};

export default StudentTimetable;
